package env

const (
	// MousePrimary specifies the primary mouse button (by default, the left one).
	MousePrimary = uint32(1)
	// MouseSecondary specifies the secondary mouse button (by default, the right one).
	MouseSecondary = uint32(2)
)

const (
	// ModControl specifies the CTRL key modifier.
	ModControl = uint32(1)
	// ModShift specifies the SHIFT key modifier.
	ModShift = uint32(2)
)
